<section id="skills" class="py-5 mt-3 py-lg-8">
    <div class="container">
        <!-- Badge -->
        <div class="title-badge d-inline-block mb-3 bg-primary text-primary bg-opacity-10 px-3 small">Skills</div>

        <div class="row row-cols-1 row-cols-md-2 mt-md-5 center-v">
            <!-- - - - - - - - -  -->
            <!-- Description -->
            <!-- - - - - - - - -  -->
            <div class="col">
                <!-- Title -->

                <h2 class="mb-4 w-80 overflow-hidden mb-5"
                    data-tor-group=".fx => scroll:[@width(0;auto) @visibility(hidden;visible), {start: /10+5/, end: /10+5/}];">
                    As a designer I am constantly improving my
                    <span class="text-primary">


                        <span class="text-nowrap"><span class="fx">s</span><span class="fx">k</span><span
                                class="fx">i</span><span class="fx">l</span><span class="fx">l</span><span
                                class="fx">s</span></span>

                        <span class="text-body text-opacity-30 ms-n1" data-tor="loop:blink">|</span>
                    </span>
                </h2>


                <!-- Description -->
                <p class="mb-6">Aenean lacinia, mauris nec rutrum consequat, ante ipsum imperdiet sapien, ultricies
                    consectetur ipsum nunc facilisis tortor. Curabitur porta blandit erat, id semper est consequat</p>

                <!-- Skill progress -->
                <div id="skill-progress">

                    <div class="skill mb-5">
                        <h3 class="h6 small font-sans-serif mb-3">App design</h3>
                        <div class="progress rounded-pill ">
                            <div class="progress-bar bg-secondary rounded-pill" role="progressbar" aria-valuenow="75"
                                aria-valuemin="0" aria-valuemax="100"
                                data-tor="inview:@width(0%;75%) exponential slower">
                                <span class="ms-auto me-3">75%</span>
                            </div>
                        </div>
                    </div>

                    <div class="skill mb-5">
                        <h3 class="h6 small font-sans-serif mb-3">Web design</h3>
                        <div class="progress rounded-pill ">
                            <div class="progress-bar bg-secondary rounded-pill" role="progressbar" aria-valuenow="95"
                                aria-valuemin="0" aria-valuemax="100"
                                data-tor="inview:@width(0%;95%) exponential slower">
                                <span class="ms-auto me-3">95%</span>
                            </div>
                        </div>
                    </div>

                    <div class="skill mb-5">
                        <h3 class="h6 small font-sans-serif mb-3">Art &amp; Illustration</h3>
                        <div class="progress rounded-pill ">
                            <div class="progress-bar bg-secondary rounded-pill" role="progressbar" aria-valuenow="65"
                                aria-valuemin="0" aria-valuemax="100"
                                data-tor="inview:@width(0%;65%) exponential slower">
                                <span class="ms-auto me-3">65%</span>
                            </div>
                        </div>
                    </div>

                    <div class="skill mb-5">
                        <h3 class="h6 small font-sans-serif mb-3">Branding</h3>
                        <div class="progress rounded-pill ">
                            <div class="progress-bar bg-secondary rounded-pill" role="progressbar" aria-valuenow="85"
                                aria-valuemin="0" aria-valuemax="100"
                                data-tor="inview:@width(0%;85%) exponential slower">
                                <span class="ms-auto me-3">85%</span>
                            </div>
                        </div>
                    </div>

                </div>
            </div>
            <!-- end .col -->

